position: relative;
  display: inline-block;
  color: $ch5-animation-color;

  /// Applied when startAnimating='true'
  .ch5-animation--start-animating-true {
    animation-play-state: running;
  }

  /// Applied when startAnimating='false'
  .ch5-animation--start-animating-false {
    animation-play-state: paused;
  }

position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    z-index: $ch5-background-z-index;
    &--parent {
        position: relative;
        z-index: $ch5-background-parent-z-index;
    }
    &--canvas {
        position: absolute;
        left: 0;
        top: 0;
        opacity: 0;
        transition-property: opacity;
        transition-duration: 1s;
        transition-timing-function: ease;

        &.ch5bg-fadein {
            opacity: 1;
        }
    }

// display: flex; // ***
		display: block; // ***
		max-width: 100%;
		cursor: pointer;
		pointer-events: all;

		button {
			cursor: pointer;
		}

		button,
		.ch5-button--label {
			background-color: transparent;
			border: none;
			color: inherit;

			&:focus {
				outline: none;
			}
		}

		/// Applied on label
		.ch5-button--label {
			display: block;
			@include text-truncate;
		}

		.cb-btn {
			display: inline-flex;
			align-items: center;
			justify-content: space-between;
			line-height: $ch5-button-content-line-height;
			padding: $ch5-button-padding;
			vertical-align: middle; //adding this property to prevent button taking extra height for iconUrlFilltype="initial" and vAlignLabel="bottom"
		}

		.cb-btn {
			padding-top: calc($ch5-button-padding / 2);
			padding-bottom: calc($ch5-button-padding / 2);
		}

		.ch5-button--label {
			padding-left: calc($ch5-button-padding / 2);
			padding-right: calc($ch5-button-padding / 2);
		}

		&.ch5-button--vertical {
			position: relative;

			.cb-btn {
				transform: rotate(270deg) translateX(-100%); // ****
				transform-origin: top left;
			}
		}

button {
			background-repeat: no-repeat;
			background-size: contain;
			background-position: center; // Center the image
		}

button {
			background-size: contain;
			background-repeat: no-repeat;
			background-position: center;
		}

.ch5-button--img {
			background-size: contain;
			background-position: center;
			background-repeat: no-repeat;
		}

.ch5-button--img {
			background-size: contain;
			background-position: center;
			background-repeat: no-repeat;
		}

height: 100%;

			.cb-btn {
				height: 100% !important;
				min-height: $ch5-button-size; // Added as a fix for the issue of button height varying when moved from width to height in stretch
			}

width: 100%;
			height: 100%;

			.cb-btn {
				width: 100% !important;
				height: 100% !important;
				min-height: $ch5-button-size; // Added as a fix for the issue of button height varying when moved from width to height in stretch
				overflow: hidden;
				text-overflow: ellipsis;
				white-space: nowrap;
			}

display: block;
		background-repeat: no-repeat;
		background-size: contain;
		background-position: center;
		color: inherit;
		height: $property-image-height * 1.5;
		width: $property-image-height * 1.5;

		&::before,
		&::after {
			content: none;
		}

display: inline-block;
	text-decoration: none;
	outline: none;
	border-style: none;
	text-align: center;
	overflow: hidden;
	@include border-radius($ch5-button-circle-border-radius);
	.cb-btn {
		display: inline-flex;
		vertical-align: middle;
		width: 100%;
		height: 100%;
	}

@include ch5-button-content($ch5-button-default-border-width,
			$ch5-button-default-border-style,
			$ch5-button-default-default-border-color,
			$ch5-button-default-default-background-color,
			$ch5-button-default-default-font-color );

		/// Applied when selected signal is applied
		&.ch5-button--selected {
			@include ch5-button-content--selected($ch5-button-default-selected-border-color,
				$ch5-button-default-selected-background-color,
				$ch5-button-default-selected-font-color );
		}

		/// Applied on press event
		&.ch5-button--pressed {
			@include ch5-button-content--pressed($ch5-button-default-pressed-border-color,
				$ch5-button-default-pressed-background-color,
				$ch5-button-default-pressed-font-color );
		}

position: relative;
  overflow: scroll;
  white-space: nowrap;
  will-change: transform;
  user-select: none;
  display: grid;
  height: 100%;
  padding: $ch5-button-list-padding;
  scrollbar-width: none; // needed to hide the firefox scrollbar 

  &.active {
    cursor: -webkit-grabbing;
  }

  &.ch5-button-list::-webkit-scrollbar {
    height: 0px;
    width: 0px;
  }

  &~.scrollbar-container {
    position: absolute;
    background-color: $ch5-scrollbar-track-color;
    border-radius: $ch5-scrollbar-border-radius;

    .scrollbar {
      position: absolute;
      will-change: width, transform;
      position: absolute;
      border-radius: $ch5-scrollbar-border-radius;
      background: $ch5-scrollbar-thumb-color;
    }
  }

  &.ch5-button-list--scrollbar-true {
    &~.scrollbar-container {
      display: block;
    }
  }

  &.ch5-button-list--scrollbar-false {
    &~.scrollbar-container {
      display: none;
    }
  }

grid-auto-flow: column;

    &.ch5-button-list--center-items-true {
      justify-content: center;
      align-content: start;
    }

    &.ch5-button-list--center-items-false {
      align-content: start;
      justify-content: start;
    }

    &.ch5-button-list--stretch-both {
      align-content: stretch;
      justify-content: stretch;
    }

    .ch5-button-list--button-container {
      height: 100%;
    }

    &.ch5-button-list--stretch-both {
      .ch5-button-list--button-container {
        width: 100%;
      }
    }

    &.ch5-button-list--scrollbar-true {
      padding-bottom: $ch5-scrollbar-width;

      &~.scrollbar-container {
        width: 100%;
        bottom: 0;
        height: $ch5-scrollbar-width;

        .scrollbar {
          height: 100%;
        }
      }
    }

    @for $var from 1 to 501 {
      &.ch5-button-list--rows-#{$var} {
        grid-template-rows: repeat($var, 1fr);
      }
    }

grid-auto-flow: row;

    &.ch5-button-list--center-items-true {
      justify-content: start;
      align-content: center;
    }

    &.ch5-button-list--center-items-false {
      align-content: start;
      justify-content: start;
    }

    &.ch5-button-list--stretch-both {
      align-content: stretch;
      justify-content: stretch;
    }

    &.ch5-button-list--scrollbar-true {
      padding-right: $ch5-scrollbar-width;

      &~.scrollbar-container {
        top: 0;
        height: 100%;
        right: 0;
        left: auto;
        width: $ch5-scrollbar-width;

        .scrollbar {
          width: 100%;
        }
      }
    }

    .ch5-button-list--button-container {
      width: 100%;
    }

    &.ch5-button-list--stretch-both {
      .ch5-button-list--button-container {
        height: 100%;
      }
    }

    @for $var from 1 to 501 {
      &.ch5-button-list--columns-#{$var} {
        grid-template-columns: repeat($var, 1fr);
      }
    }

