position: relative;
    display: inline-flex;
    min-width: $ch5-dpad-default-dimension;
    min-height: $ch5-dpad-default-dimension;
    vertical-align: top;
    /// Applied when size="regular"
    &.ch5-dpad--size-regular {
        .dpad-btn-icon {
            font-size: $ch5-dpad-icon-font-size-regular;
        }
    }

    .dpad-container {
        position: relative;
        display: flex;
        width: $ch5-dpad-default-dimension;
        height: $ch5-dpad-default-dimension;
        flex-wrap: wrap;
        vertical-align: top;
    }

    /// Applied when button in the disabled state
    &.ch5-disabled {
        pointer-events: none;
        opacity: 0.5;

        .ch5-dpad-child {
            pointer-events: none;
        }
    }

    &:not(.ch5-disabled) {
        .ch5-dpad-child.ch5-disabled {
            opacity: 0.5;
            pointer-events: none;
        }
    }

    .image-url {
        display: block;
        height: 100%;
        width: 100%;
        z-index: 1;

        &::before,
        &::after {
            content: none;
        }
    }

    /// Applied when type="default"
    &.ch5-dpad--type-default {
        @include ch5-dpad-content($ch5-dpad-default-default-background-color, $ch5-dpad-default-default-font-color);
    }

    /// Applied when type="primary"
    &.ch5-dpad--type-primary {
        @include ch5-dpad-content($ch5-dpad-primary-default-background-color, $ch5-dpad-primary-default-font-color);
    }

    /// Applied when type="info"
    &.ch5-dpad--type-info {
        @include ch5-dpad-content($ch5-dpad-info-default-background-color, $ch5-dpad-info-default-font-color);
    }

    /// Applied when type="text"
    &.ch5-dpad--type-text {
        @include ch5-dpad-content($ch5-dpad-text-default-background-color, $ch5-dpad-text-default-font-color);
    }

    /// Applied when type="danger"
