        @include ch5-dpad-content($ch5-dpad-warning-default-background-color, $ch5-dpad-warning-default-font-color);
    }

    /// Applied when type="success"
    &.ch5-dpad--type-success {
        @include ch5-dpad-content($ch5-dpad-success-default-background-color, $ch5-dpad-success-default-font-color);
    }

    /// Applied when type="secondary"
    &.ch5-dpad--type-secondary {
        @include ch5-dpad-content($ch5-dpad-secondary-default-background-color, $ch5-dpad-secondary-default-font-color);
    }

    .center {
        position: absolute;
        display: flex;
        justify-content: center;
        align-items: center;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        height: 34%;
        width: 34%;

        .dpad-btn-label {
            font-size: $ch5-dpad-label-font-size;
        }

        overflow: hidden;
        z-index: 2;
        cursor: pointer;

        &.ch5-disabled {
            pointer-events: none;
            z-index: 2;
        }

        span {
            border-radius: 50%;
            text-align: center;
            background-repeat: no-repeat;
            background-position: center;
            background-size: 80%;
        }

        .image-url {
            border-radius: 50%;
        }
    }

    .direction-btn {
        position: relative;
        display: flex;
        cursor: pointer;
        justify-content: center;
        align-items: center;

        &.ch5-disabled {
            pointer-events: none;
            z-index: 2;
        }

        span {
            font-size: 2rem;
            background-repeat: no-repeat;
            background-position: center;
            background-size: 80%;
        }
    }

