			min-width: $ch5-keypad-regular-container-width;

			.keypad-row {
				flex: 1 1;

				.keypad-btn.special-center {
					width: calc(33.334% - 4px);
				}

				.keypad-btn.special-center button {
					color: $ch5-keypad-success-default-font-color;
					background-color: $ch5-keypad-success-default-background-color;
