  user-select: none;
  will-change: transform;
  display: flex;
  height: auto;

  &.active {
    cursor: -webkit-grabbing;
  }

color: $ch5-textinput-text-color;

  &__assets {
    display: flex;
    align-items: center;
    border: $ch5-textinput-input-border-width solid $ch5-textinput-input-border-color;
    border-radius: $ch5-textinput-input-border-radius;
    height: 100%;
    width: 100%;
  }

  // text-transform
  &--uppercase {
    text-transform: uppercase;
  }

  &--lowercase {
    text-transform: lowercase;
  }

  &--capitalize {
    text-transform: capitalize;
  }

  &--none {
    text-transform: none;
  }

  &--regular {
    width: $ch5-textinput-size-regular-width;

    span {
      width: 100%;
      font-style: $ch5-textinput-regular-font-style;
      font-size: $ch5-textinput-regular-font-size;
      font-weight: $ch5-textinput-regular-font-weight;
    }
    ._ch5-textinput-mask__letter {
      font-size: $ch5-textinput-regular-mask-font-size;
    }

  }

  &--x-small {
    width: $ch5-textinput-size-x-small-width;

    span {
      width: 100%;
      font-style: $ch5-textinput-x-small-font-style;
      font-size: $ch5-textinput-x-small-font-size;
      font-weight: $ch5-textinput-x-small-font-weight;
    }
    ._ch5-textinput-mask__letter {
      font-size: $ch5-textinput-x-small-mask-font-size;
    }
  }

  &--small {
    width: $ch5-textinput-size-small-width;

    span {
      width: 100%;
      font-style: $ch5-textinput-small-font-style;
      font-size: $ch5-textinput-small-font-size;
      font-weight: $ch5-textinput-small-font-weight;
    }
    ._ch5-textinput-mask__letter {
      font-size: $ch5-textinput-small-mask-font-size;
    }
  }

  &--large {
    width: $ch5-textinput-size-large-width;

    span {
      width: 100%;
      font-style: $ch5-textinput-large-font-style;
      font-size: $ch5-textinput-large-font-size;
      font-weight: $ch5-textinput-large-font-weight;
    }
    ._ch5-textinput-mask__letter {
      font-size: $ch5-textinput-large-mask-font-size;
    }
  }

  &--x-large {
    width: $ch5-textinput-size-x-large-width;

    span {
      width: 100%;
      font-style: $ch5-textinput-x-large-font-style;
      font-size: $ch5-textinput-x-large-font-size;
      font-weight: $ch5-textinput-x-large-font-weight;
    }
    ._ch5-textinput-mask__letter {
      font-size: $ch5-textinput-x-large-mask-font-size;
    }
  }
  .ch5-textinput__assets { 
    & > span {
      display: flex;
      position: relative;
      align-items: center;
    }
  }
  &--content {
    height: 100%;
    width: 100%;
    .ch5-textinput__assets {      
      height: 100%;
      width: 100%;
      input {
        width: 100%;
        height: 100%;
      }
    }
  }
  &--width {
    width: 100%;
    .ch5-textinput__assets {
      input{
        width: 100%;
      }
    }
  }
  &--fixed {
    width: 400px;
    .ch5-textinput__assets {
      input {
        width: 100%;
      }
    }
  }

