display: inline-block;
  position: relative;
  overflow: hidden;
  background: #000;

  img {
    height: 100%;
    width: 100%;
  }

  img.hide {
    display: none;
  }

  .full-screen-icon {
    display: flex;
    justify-content: center;
    align-items: center;
    position: absolute;
    right: 5px;
    top: 5px;
    padding: 2px;
    opacity: 1;
    border-radius: 20px;
    background-color: rgb(0, 0, 0, 0.2);
    transition: opacity 0.2s ease-in;

    &.hide {
      opacity: 0 !important;
      pointer-events: none !important;
    }

    .svgIconStyle {
      padding: 2px;
      width: 32px;
      height: 32px;
      fill: #fff;
    }

    &:hover {
      text-decoration: none;
    }
  }

