position: relative;
  display: inline-block;
  vertical-align: top;
  // padding: 0px 15px 30px 15px;
  font-size: $ch5-slider-font-size;
  font-style: $ch5-slider-font-style;
  font-weight: $ch5-slider-font-weight;

  &.ch5-hide-dis {
    display: none;
  }

  &:focus, .noUi-connects:focus, .noUi-base, .noUi-handle:focus{
    outline: none;
  }

  .noUi-target {
    display: inline-block;
  }

  &[nohandle] .noUi-handle {
    visibility: hidden;
  }

  /// class apply when slider orientation="horizontal"
  &.ch5-slider--orientation--horizontal {
    .noUi-target {
      height: $ch5-slider-target-size;
    }
  }

  /// class apply when slider orientation="vertical"
  &.ch5-slider--orientation--vertical {
    .noUi-target {
      width: $ch5-slider-target-size;
    }
  }
  &.ch5-slider--disabled {
    .noUi-connect {
      background: lighten($ch5-slider-connect-bg, $ch5-slider-background-lighten-percent);
    }
  }
  .noUi-handle {
    &:before, &:after {
      content: none;
    }
  }

  /* ===============================================
    COLORS
   ============================================= */
  .noUi-target {
    background: $ch5-slider-target-bg;
    border-style: $ch5-slider-target-border-style;
    border-color: $ch5-slider-target-border-color;
    border-width: $ch5-slider-target-border-width;
    box-shadow: none;
  }
  .noUi-connect {
    background: $ch5-slider-connect-bg;
  }
  .noUi-handle{
    border-style: $ch5-slider-handle-border-style;
    border-color: $ch5-slider-handle-border-color;
    border-width: $ch5-slider-handle-border-width;
    background: $ch5-slider-handle-bg;
    box-shadow: none;
  }

  /* ===============================================
    STRETCH
   ============================================= */
  &.ch5-slider--stretch--width.ch5-slider--orientation--horizontal{
    width: 100% !important;
  }

  &.ch5-slider--stretch--height.ch5-slider--orientation--vertical{
    height: 100% !important;
  }


  &.ch5-slider--stretch--height.ch5-slider--orientation--horizontal {
    height: 100% !important;
    .noUi-target {
      top: 50%;
      transform: translateY(-50%);
    }
  }

  &.ch5-slider--stretch--width.ch5-slider--orientation--vertical {
    width: 100% !important;
    .noUi-target {
      left: 50%;
      transform: translateX(-50%);
    }
  }

  &.ch5-slider--stretch--both.ch5-slider--orientation--horizontal{
    width: 100% !important;
    height: 100% !important;

    .noUi-target {
      top: 50%;
      transform: translateY(-50%);
    }
  }

  &.ch5-slider--stretch--both.ch5-slider--orientation--vertical{
    height: 100% !important;
    width: 100% !important;

    .noUi-target {
      left: 50%;
      transform: translateX(-50%);
    }
  }

