position: relative;
  display: inline-block;
  color: var(--ch5-animation--color);

  /// Applied when startAnimating='true'
  .ch5-animation--start-animating-true {
    animation-play-state: running;
  }

  /// Applied when startAnimating='false'
  .ch5-animation--start-animating-false {
    animation-play-state: paused;
  }

position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: -1;

  /// @require .ch5-background
  /// Added automatically on the first parent of the ch5-background element
  &--parent {
    position: relative;
    z-index: 1;
  }

  /// @require .ch5-background
  /// Added automatically on the all canvas child of ch5-background element
  &--canvas {
    position: absolute;
    left: 0;
    top: 0;
    opacity: 0;
    transition-property: opacity;
    transition-duration: var(--ch5-background--transition-duration);
    transition-timing-function: var(--ch5-background--transition-timing-function);

    &.ch5bg-fadein {
      opacity: 1;
    }
  }

position: absolute;
    left: 0;
    top: 0;
    opacity: 0;
    transition-property: opacity;
    transition-duration: var(--ch5-background--transition-duration);
    transition-timing-function: var(--ch5-background--transition-timing-function);

    &.ch5bg-fadein {
      opacity: 1;
    }

display: block;
    max-width: 100%;
    cursor: pointer;
    pointer-events: all;

    button {
      cursor: pointer;
    }

    button,
    .ch5-button--label {
      background-color: transparent;
      border: none;
      color: inherit;

      &:focus {
        outline: none;
      }
    }

    /// Applied on label
    .ch5-button--label {
      display: block;
      @include text-truncate();
    }

    .cb-btn {
      display: inline-flex;
      align-items: center;
      justify-content: space-between;
      font-style: var(--ch5-button--font-style);
      font-weight: var(--ch5-button--font-weight);
      line-height: var(--ch5-button--content-line-height);
      padding: var(--ch5-button--padding);
      vertical-align: middle; //adding this property to prevent button taking extra height for iconUrlFilltype="initial" and vAlignLabel="bottom"
    }

    .cb-btn {
      padding-top: calc(var(--ch5-button--padding) / 2);
      padding-bottom: calc(var(--ch5-button--padding) / 2);
    }

    .ch5-button--label {
      padding-left: calc(var(--ch5-button--padding) / 2);
      padding-right: calc(var(--ch5-button--padding) / 2);
    }

    /// Applied when button orientation='vertical'
    &.ch5-button--vertical {
      position: relative;

      .cb-btn {
        transform: rotate(270deg) translateX(-100%);
        transform-origin: top left;
      }
    }

position: relative;

      .cb-btn {
        transform: rotate(270deg) translateX(-100%);
        transform-origin: top left;
      }

button {
      background-repeat: no-repeat;
      background-size: contain;
      background-position: center; // Center the image
    }

button {
      background-size: contain;
      background-repeat: no-repeat;
      background-position: center;
    }

.ch5-button--img {
      background-size: contain;
      background-position: center;
      background-repeat: no-repeat;
    }

.ch5-button--img {
      background-size: contain;
      background-position: center;
      background-repeat: no-repeat;
    }

/// Applied to make the width as much as its parent allows.
    &.ch5-button--stretch-width {
      width: 100%;

      .cb-btn {
        width: 100% !important;
      }
    }

    /// Applied to make the height as much as its parent allows.
    &.ch5-button--stretch-height {
      height: 100%;

      .cb-btn {
        height: 100% !important;
        min-height: var(--ch5-button--size); // Added as a fix for the issue of button height varying when moved from width to height in stretch
      }
    }

    /// Applied to make both width and height as much as its parent allows.
    &.ch5-button--stretch-both {
      width: 100%;
      height: 100%;

      .cb-btn {
        width: 100% !important;
        height: 100% !important;
        min-height: var(--ch5-button--size); // Added as a fix for the issue of button height varying when moved from width to height in stretch
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }
    }

