@include ch5-button-content(
      --ch5-button--default-border-width,
      --ch5-button--default-border-style,
      --ch5-button--default-border-color,
      --ch5-button--default-background-color,
      --ch5-button--default-font-color
    );

    /// Applied when selected signal is applied
    &.ch5-button--selected {
      @include ch5-button-content--selected(--ch5-button--default-selected-border-color, --ch5-button--default-selected-background-color, --ch5-button--default-selected-font-color);
    }

    /// Applied on press event
    &.ch5-button--pressed {
      @include ch5-button-content--pressed(--ch5-button--default-pressed-border-color, --ch5-button--default-pressed-background-color, --ch5-button--default-pressed-font-color);
    }

position: relative;
  overflow: scroll;
  white-space: nowrap;
  will-change: transform;
  user-select: none;
  display: grid;
  height: 100%;
  padding: var(--ch5-button-list--container-padding);
  scrollbar-width: none; // needed to hide the firefox scrollbar

  &.active {
    cursor: -webkit-grabbing;
  }

  &.ch5-button-list::-webkit-scrollbar {
    height: 0px;
    width: 0px;
  }

  &~.scrollbar-container {
    position: absolute;
    background-color: var(--ch5-button-list--scrollbar-track-color);
    border-radius: var(--ch5-button-list--scrollbar-border-radius);

    .scrollbar {
      position: absolute;
      will-change: width, transform;
      position: absolute;
      border-radius: var(--ch5-button-list--scrollbar-border-radius);
      background: var(--ch5-button-list--scrollbar-thumb-color);
    }
  }

  &.ch5-button-list--scrollbar-true {
    &~.scrollbar-container {
      display: block;
    }
  }

  &.ch5-button-list--scrollbar-false {
    &~.scrollbar-container {
      display: none;
    }
  }

grid-auto-flow: column;

    &.ch5-button-list--center-items-true {
      justify-content: center;
      align-content: start;
    }

    &.ch5-button-list--center-items-false {
      align-content: start;
      justify-content: start;
    }

    &.ch5-button-list--stretch-both {
      align-content: stretch;
      justify-content: stretch;
    }

    .ch5-button-list--button-container {
      height: 100%;
    }

    &.ch5-button-list--stretch-both {
      .ch5-button-list--button-container {
        width: 100%;
      }
    }

    &.ch5-button-list--scrollbar-true {
      padding-bottom: var(--ch5-button-list--scrollbar-width);

      &~.scrollbar-container {
        width: 100%;
        bottom: 0;
        height: var(--ch5-button-list--scrollbar-width);

        .scrollbar {
          height: 100%;
        }
      }
    }

    @for $var from 1 to 501 {
      &.ch5-button-list--rows-#{$var} {
        grid-template-rows: repeat($var, 1fr);
      }
    }

grid-auto-flow: row;

    &.ch5-button-list--center-items-true {
      justify-content: start;
      align-content: center;
    }

    &.ch5-button-list--center-items-false {
      align-content: start;
      justify-content: start;
    }

    &.ch5-button-list--stretch-both {
      align-content: stretch;
      justify-content: stretch;
    }

    &.ch5-button-list--scrollbar-true {
      padding-right: var(--ch5-button-list--scrollbar-width);

      &~.scrollbar-container {
        top: 0;
        height: 100%;
        right: 0;
        left: auto;
        width: var(--ch5-button-list--scrollbar-width);

        .scrollbar {
          width: 100%;
        }
      }
    }

    .ch5-button-list--button-container {
      width: 100%;
    }

    &.ch5-button-list--stretch-both {
      .ch5-button-list--button-container {
        height: 100%;
      }
    }

    @for $var from 1 to 501 {
      &.ch5-button-list--columns-#{$var} {
        grid-template-columns: repeat($var, 1fr);
      }
    }

