  &.ch5-dpad--size-regular {
    .dpad-btn-icon {
      font-size: var(--ch5-dpad--icon-size-regular);
    }
  }

  .dpad-container {
    position: relative;
    display: flex;
    width: var(--ch5-dpad--regular-size);
    height: var(--ch5-dpad--regular-size);
    flex-wrap: wrap;
    vertical-align: top;
  }

  /// Applied when button in the disabled state
  &.ch5-disabled {
    @include ch5-component-disabled();

    .ch5-dpad-child {
      pointer-events: none;
    }
  }

  &:not(.ch5-disabled) {
    .ch5-dpad-child.ch5-disabled {
      @include ch5-component-disabled();
    }
  }

  .image-url {
    display: block;
    height: 100%;
    width: 100%;
    z-index: 1;

    &::before,
    &::after {
      content: none;
    }
  }

  @each $color in $dpadColorStyles {
    ///! 2. Applied when type="#{$color}"
    $backgroundColor: "--ch5-dpad--" + $color + "-background-color";
    $fontColor: "--ch5-dpad--" + $color + "-font-color";
    &.ch5-dpad--type-#{$color} {
      @include ch5-dpad-content(#{$backgroundColor}, #{$fontColor});
    }
  }

