  .center {
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    height: 34%; // increasing slightly to avoid white line from 33.33%
    width: 34%; // increasing slightly to avoid white line

    .dpad-btn-label {
      font-size: var(--ch5-dpad--label-font-size);
    }

    overflow: hidden;
    z-index: 2;
    cursor: pointer;

    &.ch5-disabled {
      pointer-events: none;
      z-index: 2;
    }

    span {
      border-radius: 50%;
      text-align: center;
      background-repeat: no-repeat;
      background-position: center;
      background-size: 80%;
    }

    .image-url {
      border-radius: 50%;
    }
  }

  .direction-btn {
    position: relative;
    display: flex;
    cursor: pointer;
    justify-content: center;
    align-items: center;

    &.ch5-disabled {
      pointer-events: none;
      z-index: 2;
    }

    span {
      font-size: 2rem;
      background-repeat: no-repeat;
      background-position: center;
      background-size: 80%;
    }
  }

  /// Defines the dpad shape plus
  &.ch5-dpad--shape-plus {
    /// Applied when disableCenterButton="true"
    .ch5-disable-child-button {
      pointer-events: none; // Do not set opacity here
      cursor: default;
    }
    .dpad-container {
      .ch5-disable-child-button > span {
        @include ch5-component-disabled();
        cursor: default;
      }
      .ch5-hide-child-button {
        cursor: default;
        > span {
          visibility: hidden !important;
          // opacity: 0.5;
          cursor: default;
        }
      }
    }

    .direction-btn {
      flex: 33.33%;
      height: 33.33%;

      &:after,
      &:before {
        content: "";
        height: 20px;
        width: 20px;
        position: absolute;
        background-color: transparent;
      }

      span {
        height: 100%;
        width: 100%;
        position: relative;
        display: inline-flex;
        justify-content: center;
        align-items: center;
        z-index: 1;
        line-height: calc(var(--ch5-dpad--regular-size) / 3);
        text-align: center;
      }
