      }

      &.down {
        @include ch5-dpad-content-plus-bottom(--ch5-dpad--regular-border-radius, --ch5-dpad--danger-background-color);
      }

      /// Applied when up button is in the pressed state
      &.ch5-dpad-button-up--pressed {
