  .ch5-toggle__label {
    display: inline-flex;
    vertical-align: middle;
    font-style: var(--ch5-toggle--label-font-style);
    font-size: var(--ch5-toggle--label-font-size);
    font-weight: var(--ch5-toggle--label-font-weight);
    color: var(--ch5-toggle--label-font-color);
    margin: var(--ch5-toggle--label-margin);

    &[hidden] {
      display: none;
    }
  }

  .ch5-toggle__on-label,
  .ch5-toggle__off-label {
    word-wrap: normal;
    color: var(--ch5-toggle--text-font-color);
    font-style: var(--ch5-toggle--text-font-style);
    font-size: var(--ch5-toggle--text-font-size);
    font-weight: var(--ch5-toggle--text-font-weight);
  }

  /// Applied for on label
  .ch5-toggle__on-label,
  /// Applied for off label
  .ch5-toggle__off-label,
  /// Applied for icon-on
  .ch5-toggle__on-icon,
  /// Applied for icon-off
  .ch5-toggle__off-icon {
    margin: 0 2px 0 2px;
  }

  .ch5-toggle__on-icon,
  .ch5-toggle__off-icon {
    color: var(--ch5-toggle--icon-font-color);
    font-size: var(--ch5-toggle--icon-font-size);
  }

  /* ch5-toggle - handle default */
  /// Applied on the handle
  .ch5-toggle__handle {
    position: relative;
    overflow: hidden;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-width: 100px;
    padding: 0;
    transition: all 0.4s ease-out;
    aspect-ratio: 3/1;

    /* ch5-toggle - on/off container */
    div {
      position: relative;
      z-index: 3;
      line-height: 2em;
      text-align: center;
      user-select: none;
      opacity: 0;
      height: 100%;
      display: flex;
      justify-content: center;
      align-items: center;

      &:first-of-type {
        opacity: 1;
      }
    }

    /* ch5-toggle - knob */
    a {
      position: absolute;
      right: 50%;
      top: 0;
      z-index: 2;
      display: block;
      width: 50%;
      height: 100%;
      padding: 0;
      transition: all 0.2s ease-out;
    }
  }

  /* ch5-toggle - circle shape */
  &.ch5-toggle--circle {
    .ch5-toggle__handle {
      border-radius: 1.75em;
      background-color: var(--ch5-toggle--circle-handle-border-color);
      margin: var(--ch5-toggle--handle-circle-margin);

      div {
        width: 100%;
        transition: all 0.2s ease-out;

        &:first-of-type {
          color: var(--ch5-toggle--circle-label-off-color);
          text-align: center;
          padding-left: 1.75em;
        }

        &:last-of-type {
          display: none;
          color: var(--ch5-toggle--circle-label-on-color);
          text-align: center;
          padding-right: 1.75em;
        }
      }

      a {
        left: 0;
        top: 0;
        width: 33%;
        border-radius: 100%;
        background-color: var(--ch5-toggle--circle-handle-background-color);
        border: 0.25em solid var(--ch5-toggle--circle-handle-border-color);
      }
    }
  }

  /* ch5-toggle - rectangle shape */
  &.ch5-toggle--rectangle {
    .ch5-toggle__handle {
      background: var(--ch5-toggle--handle-rectangle-background);
      border-radius: 1px;
      box-shadow: inset rgba(0, 0, 0, 0.1) 0 1px 0;
      margin: var(--ch5-toggle--handle-rectangle-margin);

      div {
        display: inline-flex;
        width: 50%;
        transition: all 0.1s;

        &:first-of-type {
          color: var(--ch5-toggle--rectangle-label-off-color);
        }

        &:last-of-type {
          color: var(--ch5-toggle--rectangle-label-on-color);
        }
      }

      a {
        border-radius: 1px;
        background-color: var(--ch5-toggle--rectangle-handle-background-color);
      }
    }
  }

  /* ch5-toggle - checked/on */
  /// Applied when value is true
  &.ch5-toggle--on {
    .ch5-toggle__handle {
      a {
        right: 0%;
      }

      div:first-of-type {
        opacity: 0;
      }

      div:last-of-type {
        opacity: 1;
      }
    }

    /* ch5-toggle - circle shape checked/on */
    /// Class applied when shape='circle'
    &.ch5-toggle--circle {
      .ch5-toggle__handle {
        // box-shadow: inset 0 0 0 30px var(--ch5-toggle-handle-checked-color;
        background-color: var(--ch5-toggle--circle-handle-selected-background-color);
        transition: background-color 0.3s linear;

        div:first-of-type {
          display: none;
        }

        div:last-of-type {
          display: flex;
        }

        a {
          left: 100%;
          margin-left: -33%;
          background-color: var(--ch5-toggle--circle-handle-background-color);
          border-color: var(--ch5-toggle--circle-handle-selected-background-color);
        }
      }
    }

    /* ch5-toggle - rectangle shape checked/on */
    /// Class applied when shape='rectangle'
    &.ch5-toggle--rectangle {
      .ch5-toggle__handle {
        div {
          right: 0;
        }

        a {
          background-color: var(--ch5-toggle--rectangle-handle-selected-background-color);
        }
      }
    }
  }

  /// Class applied when orientation='horizontal'
  &.ch5-toggle--horizontal {
    // Placeholder
  }

  /// Class applied when orientation='vertical'
  &.ch5-toggle--vertical {
    > div {
      padding: 50% 0;
      height: 0;
      -webkit-transform: rotate(90deg);
      transform: rotate(90deg);
    }
  }

  // 'rtl' support
  &.ch5-toggle--dir--rtl {
    .ch5-toggle__handle a {
      left: 50%;
      right: auto;
    }

    &.ch5-toggle--circle .ch5-toggle__handle div:first-of-type {
      padding-left: 0;
      padding-right: 1.75em;
    }

    &.ch5-toggle--circle .ch5-toggle__handle div:last-of-type {
      padding-right: 0;
      padding-left: 1.75em;
    }

    &.ch5-toggle--circle .ch5-toggle__handle a {
      right: auto;
      left: 100%;
      margin-left: -33%;
    }

    &.ch5-toggle--on {
      .ch5-toggle__handle a {
        left: 0;
        right: auto;
      }
    }

    &.ch5-toggle--on.ch5-toggle--circle {
      .ch5-toggle__handle a {
        left: 33%;
      }
    }
  }

